import java.awt.*;import java.awt.event.*;import javax.swing.*;// This class contains a little blurb explaining who programmed the game.public class AboutWindow extends JFrame {	public static final int WIDTH = 300;	public static final int HEIGHT = 200;		AboutWindow () {		super ();			setSize (WIDTH, HEIGHT);		setTitle ("About");				Container content = getContentPane();				JLabel name = new JLabel ("K. Rebecca Langer", JLabel.CENTER);		JLabel num = new JLabel ("Student Number:  4873715", JLabel.CENTER);		JLabel date = new JLabel ("Submitted:  March 29, 2005", JLabel.CENTER);				content.setLayout(new GridLayout(3, 1));				content.add (name);		content.add (num);		content.add (date);				setVisible (true);	}	}