import java.awt.*;import java.awt.event.*;import javax.swing.*;// This class manages the panel of 'bet' and 'fold' buttons in the lower left// hand corner of the screen.public class BottomButtonPanel extends JPanel implements ActionListener {	// JButtons are buttons for betting and folding	private JButton betButton;	private JButton foldButton;	// MainPokerWindow links back to the main window	MainPokerWindow parentWindow;	// Constructor creates new button panel with no actual buttons in it yet.	BottomButtonPanel (MainPokerWindow par) {			super ();		parentWindow = par;		setBackground (Color.black);			}		// This method creates the betting and folding buttons and adds them to	// the panel.	public void setBetting () {					setLayout (new GridLayout (1,3));		betButton = new JButton ("Bet");		betButton.addActionListener(this);							foldButton = new JButton ("Fold");		foldButton.addActionListener(this);		add (betButton);		add (foldButton);				}			// This method is called whenever someone presses one of the buttons.	public void actionPerformed (ActionEvent e) {		// If the betting button is pressed both buttons are temporatily		// inactivated and the userBet() function in MainPokerWindow is		// called       	if (e.getActionCommand( ).equals("Bet")){       		inactivateButtons();       		parentWindow.userBet ();       	}	   			   	// If the folding button is pressed the userFold() function in	   	// MainPokerWindow is called		else if (e.getActionCommand( ).equals("Fold"))			parentWindow.userFold ();				// If this method somehow gets called without either the 'bet'		// or 'fold' buttons being pressed an error message is printed		// to the terminal       	else	   		System.out.println("Error in button interface.");        }	// This method inactivates the 'bet' and 'fold' buttons.	public void inactivateButtons () {		//System.out.println ("deactivating buttons");				betButton.setEnabled (false);		foldButton.setEnabled (false);		}			// This method (re)activates the 'bet' and 'fold' buttons.	public void activateButtons () {		//System.out.println ("activating buttons");				betButton.setEnabled (true);		foldButton.setEnabled (true);		}			// This method makes the buttons visible.	public void makeVisible () {			betButton.setVisible (true);		foldButton.setVisible (true);		}			// This method makes the buttons invisible.	public void makeInvisible () {			betButton.setVisible (false);		foldButton.setVisible (false);		}			}