// Cisc 124// K. Rebecca Langer// Student #:  4873715// This class contains the code for the object 'card'.  Each card has variables// indicating its suit and number.class Card {int suit;int number;			// Constructor method.		// default constructor creates the ace of spades.	public Card() {		this(1, 1);	}			// Constructor method specifically designed to be called by PokerDeck	// constructor method.	public Card(int i, int j) {		suit = i;		number = j;		}			// This method returns the name of the card in string form.	public String toString() {		String suitName = new String();		String numberName = new String();			switch (suit)		{			case 1 : suitName = "spades"; break;			case 2 : suitName = "diamonds"; break;			case 3 : suitName = "clubs"; break;			case 4 : suitName = "hearts"; break;		}			switch (number)		{			case 1 : numberName = "ace"; break;			case 2 : numberName = "two"; break;			case 3 : numberName = "three"; break;			case 4 : numberName = "four"; break;			case 5 : numberName = "five"; break;			case 6 : numberName = "six"; break;			case 7 : numberName = "seven"; break;			case 8 : numberName = "eight"; break;			case 9 : numberName = "nine"; break;			case 10 : numberName = "ten"; break;			case 11 : numberName = "jack"; break;			case 12 : numberName = "queen"; break;			case 13 : numberName = "king"; break;		}				return ("the " + numberName + " of " + suitName);	}		public int getNumber () {		return number;	}	// demo method to test functionality.		public static void main (String args[]) {		Card demoCard = new Card (3, 4);	System.out.println( demoCard );		}			// returns a code corresponding to the name of the appropriate card graphic	// in file.	public String graphicCode () {			String suitName = new String();		String numberName = new String();			switch (suit)		{			case 1 : suitName = "s"; break;			case 2 : suitName = "d"; break;			case 3 : suitName = "c"; break;			case 4 : suitName = "h"; break;		}			switch (number)		{			case 1 : numberName = "1"; break;			case 2 : numberName = "2"; break;			case 3 : numberName = "3"; break;			case 4 : numberName = "4"; break;			case 5 : numberName = "5"; break;			case 6 : numberName = "6"; break;			case 7 : numberName = "7"; break;			case 8 : numberName = "8"; break;			case 9 : numberName = "9"; break;			case 10 : numberName = "10"; break;			case 11 : numberName = "j"; break;			case 12 : numberName = "q"; break;			case 13 : numberName = "k"; break;		}				return (suitName + numberName);				}						}