import java.awt.*;import java.awt.event.*;import javax.swing.*;import javax.swing.event.*;// This class creates a panel that explains who won and why and allows them// to proceed to a new QueryPanel.public class EndPanel extends JPanel implements ActionListener {	// MainPokerWindow links back to the main window	private MainPokerWindow parentWindow;	// Constructor creates panel with given String and an 'okay' button.	EndPanel (String msg, MainPokerWindow par) {			super ();			parentWindow = par;		setBackground (Color.blue);		setLayout (new GridLayout (2, 1));		JLabel question = new JLabel (msg, JLabel.CENTER);		question.setForeground (Color.white);		add (question);			JPanel subPanel = new JPanel ();		subPanel.setBackground (Color.blue);		add (subPanel);				JButton ok = new JButton ("Okay");			ok.addActionListener(this);			subPanel.add (ok);		}			// This method launches a new QueryPanel when the 'okay' button is pressed.	public void actionPerformed (ActionEvent e) {       	//System.out.println ("Okay!");       	parentWindow.launchQueryPanel (this);       	    }     }