import java.awt.*;import java.awt.event.*;import javax.swing.*;// This class contains the code for setting up and maintaining the appearance// of the poker table.public class MidPanel extends PlayerPanel {	// JLabel displays how much money is on the table	private JLabel potMoney;		MidPanel () {		super ();				setBackground (Color.blue);		setLayout (new GridLayout (1, 3));								potMoney = new JLabel ( "Pot Money:  $" + 0, JLabel.CENTER );			potMoney.setForeground (Color.white);			add (potMoney);									// inserts special panel for holding cards and initializes			// its 5 (currently invisible) cards			JPanel cardPanel = new JPanel ();			cardPanel.setBackground (Color.blue);			cardPanel.setLayout (new FlowLayout ());			add (cardPanel);							for (int i=0; i<card.length; i++){					card[i] = new CardImage ();					card[i].setVisible (false);					cardPanel.add (card[i]);				}						// inserts an inert picture of a deck of cards					ImageIcon deckImage = new ImageIcon ("cards/deck.jpg");				JLabel deck = new JLabel (deckImage, JLabel.CENTER);			add (deck);				}	// This method alters JLabel userMoney to display the amount of money given in	// the parameter newMoney.	public void setNewMoney (int newMoney) {		potMoney.setText ( "Pot Money:  $" + newMoney);			}	}