import java.awt.*;import java.awt.event.*;import javax.swing.*;// This class contains all the images needed to animate the player's poker// opponent and keeps track of the opponent's mood.public class Opponent extends JLabel {	// String keeps track of the opponent's name	private String name;		// String keeps track of the opponent's mood	private String mood;		// ImageIcons store animation frames:	private ImageIcon neutral;	private ImageIcon blink;	private ImageIcon pleased;	private ImageIcon shocked;	private ImageIcon worried;		// Constructor puts proper values to object's variables.	Opponent (String oppName) {		name = oppName;		mood = new String ("neutral");		neutral = new ImageIcon (name + "/neutral.jpg");		blink = new ImageIcon (name + "/blink.jpg");		pleased = new ImageIcon (name + "/pleased.jpg");		worried = new ImageIcon (name + "/worried.jpg");		shocked = new ImageIcon (name + "/shocked.jpg");			}		public String getName () {		return (name);	}		public ImageIcon getNeutral () {			return (neutral);			}	public ImageIcon getBlink () {			return (blink);			}		public ImageIcon getPleased () {			return (pleased);			}		public ImageIcon getWorried () {			return (worried);			}			public ImageIcon getShocked () {			return (shocked);			}	// sets the opponent's mood to something appropriate for a given hand,	// and returns the opponent's current mood.	public String analyzeHand (PokerHand hand) {		if (hand.highCard().getNumber() > 9)		setMood ("pleased");	else if (hand.highCard().getNumber() < 8)		setMood ("worried");	return (mood);	}			public String getMood () {			return (mood);		}			public void setMood (String newMood) {			mood = newMood;		}}