import java.awt.*;import java.awt.event.*;import javax.swing.*;import javax.swing.event.*;// This class creates a panel that asks the user if they want to play a game// of poker.  If they press the 'yes' button it launches a new game, and if they// press the 'no' button it exits the program.public class QueryPanel extends JPanel implements ActionListener {	// MainPokerWindow links back to the main window	private MainPokerWindow parentWindow;	// Constructor	QueryPanel (MainPokerWindow par) {		super ();		parentWindow = par;	setBackground (Color.blue);	setLayout (new GridLayout (2, 1));	JLabel question = new JLabel ("Play a game of Poker?", JLabel.CENTER);	question.setForeground (Color.white);	add (question);		JPanel subPanel = new JPanel ();	subPanel.setBackground (Color.blue);	subPanel.setLayout (new FlowLayout ());	add (subPanel);					JButton yes = new JButton ("Yes");		yes.addActionListener(this);		subPanel.add (yes);			JButton no = new JButton ("No");		no.addActionListener(this);		subPanel.add (no);			}			// This method launches a new game of poker if the user pressed 'yes' or	// quits the program if they pressed 'no'.	public void actionPerformed (ActionEvent e) {       	if (e.getActionCommand( ).equals("No"))	   		System.exit (0);       	else if (e.getActionCommand( ).equals("Yes"))			parentWindow.newGame ();       	else	   		System.out.println("Error in button interface.");    }				}