import java.util.Date;import java.awt.*;import java.awt.event.*;import javax.swing.*;// This class creates a panel containing the opponent's cards, money and// animated portraitpublic class TopPanel extends PlayerPanel {	// JLabel displays how much money the computer currently has in the hole	private JLabel oppMoney;		// AnimatedPanel creates animated portrait of the user's opponent	public AnimatedPanel oppPanel;		// Opponent contains vital info for user's opponent	private Opponent opp;				// Constructor initializes all object's variables and creates a pretty,	// play-ready layout.	TopPanel (Opponent oppInput) {		super ();				setLayout (new GridLayout (1, 2));				JPanel leftPanel = new JPanel ();		add (leftPanel);		leftPanel.setBackground (Color.black);				opp = oppInput;				oppPanel = new AnimatedPanel (opp);		add (oppPanel);				leftPanel.setLayout (new GridLayout (2, 1));					JPanel topPanel = new JPanel ();			leftPanel.add (topPanel);			topPanel.setBackground (Color.black);						oppMoney = new JLabel ( opp.getName() + "'s Money:  $" + 1000, JLabel.CENTER );			oppMoney.setForeground (Color.white);			leftPanel.add (oppMoney);					topPanel.setLayout (new FlowLayout ());							for (int i=0; i<2; i++){					card[i] = new CardImage ();					card[i].setVisible (false);					topPanel.add (card[i]);				}			}			// This method alters JLabel oppMoney to display the amount of money given in	// the parameter newMoney.	public void setNewMoney (int newMoney) {				oppMoney.setText ( opp.getName () + "'s Money:  $" + newMoney );				}	}